import { createStore } from 'vuex'

export default createStore({
    state: {
        cartList: [], // 购物车的数据
        isDelete: true, // 确定header组件 点击编辑的时候 底部组件的展示
        edit: true, // 
        orderList: [],
        userAddress:[
            {
                id: 1001,
                name: 'Yeluzi001',
                tel: '1234567890X',
                province: '江苏省',
                city: '南通市',
                county: '如皋市',
                addressDetail: '高新技术产业开发区',
                isDefault: true,
                areaCode: '110101',
              },
              {
                id: 1002,
                name: 'Yeluzi002',
                tel: '1234567890Y',
                province: '湖北省',
                city: '武汉市',
                county: '武昌区',
                addressDetail: '黄鹤楼',
                isDefault: false,
                areaCode: '110101',
              },
        ],
        orderListEnd:[],
    },
    mutations: {
        addCart(state, value) {
            state.cartList = value
        },
        changeDelete(state) {
            state.isDelete = !state.isDelete
        },
        delete(state, value) {
            state.cartList = value
        },
        edit(state, value) {
            if (value) {
                state.edit = true
            } else {
                state.edit = !state.edit
            }
        },
        pay(state, value) {
            state.orderList = value
        },
        orederListEnd(state){
            state.orderListEnd = state.orderListEnd.concat(state.orderList)
        }

    },
    actions: {}
})
